Select ad_contato.cdcliente, ad_contato.cdcontato, ad_contato.nmcontato, ad_contato.cdcontatosuperior, ad_contatosuperior.nmcontato as nmcontatosuperior, ad_contato.cdcargo, ad_cargo.nmcargo from ad_contato left join ad_contato as ad_contatosuperior on ad_contato.cdcontatosuperior = ad_contatosuperior.cdcontato left join ad_cargo on ad_contato.cdcargo = ad_cargo.cdcargo where ad_contato.nrmatricula = '{vlinformacaoadicional2}'